mapper.update:
UPDATE eboard
SET eboardtitle = #{eboardtitle}, eboardcontent = #{eboardcontent}, eventcon = #{eventcon}
WHERE eboardnum = #{eboardnum}

	UPDATE eboard
    SET eboardcount = eboardcount + 1
    WHERE eboardnum = #{eboardnum}

update events
set winner = #{winner}
where eventnum=#{eventnum};
